import React, { Component } from 'react';
import Link from 'next/link'
import Button from '../node_modules/react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import Form from '../node_modules/react-bootstrap/Form';
import InputGroup from '../node_modules/react-bootstrap/InputGroup'

export default class Groupregistration extends Component {

    constructor(props) {
        super(props);
        this.handlesubmit = this.handlesubmit.bind(this);
    }

    handlesubmit(e){
        e.preventDefault();
        const method = "POST";
        const body = JSON.stringify({name: information.name.value,email_address: information.email_address.value,password: information.password.value,limit: information.limit.value,});
        //console.log(body);
        fetch('https://uzi8fe1wu4.execute-api.eu-west-1.amazonaws.com/register_place/register_place',{method: "POST",body: body})
        .then((response) => response.json())
        .then((responseJson) => {
        //console.log(responseJson);
            if(responseJson.result == 2){
                window.alert("あなたのトークンは"+responseJson.password_token+"です。必ずメモをとってください。")
            }else{
                window.alert("あなたの団体は登録済みです。ログインしてください。")
            }
            location.href = "/group_log_in";
        })
      .catch((error) =>{
        window.alert("登録時にエラーが起きました。もう一度入力してください。")
        location.href = "/group_registration"
        //console.error('error');
      });
    }

    render(){
        return(<div className='text-center'>
            <p><h1>Group registration</h1></p>
            <p>　</p>
            <p><div><h2>"サービス名"へようこそ</h2></div></p>
            <p><div><h4>登録情報を入力してください</h4></div></p>
            <p><Form.Group controlId="information" onSubmit={this.handlesubmit}>
                <Form.Label>団体名</Form.Label>
                <Form.Control type="text" name="name" placeholder="Enter your group name" required></Form.Control>

                <Form.Label>メールアドレス</Form.Label>
                <Form.Control type="email" name="email_address" required></Form.Control>

                <Form.Label>パスワード</Form.Label>
                <Form.Control type="text" name="password" minLength="5" required></Form.Control>
                <Form.Label>同時に活動できる人数</Form.Label>
                <Form.Control type="number" name="limit" min="1" required></Form.Control>
                <Button variant='info' type="submit">新規登録</Button>
            </Form.Group></p>

            <Form model="user" onSubmit={this.handleSubmit}>
              <Form.Group controlId="name" >
                <Form.Label>firstName</Form.Label>
                <Form.Control
                  type="text"
                  model="user.name"
                  className="form-control"
                  validateOn="blur" />
                <Errors
                  model="user.name"
                  messages={{
                      isRequired: '入力必須項目です！'
                  }}
                />
              </Form.Group>
            </Form>
            <p><div>
                <Link href = "/group_log_in">
                    <a>ログイン</a>
                </Link>
            </div>
            <div>
                <Link href = "/">
                    <a>Go Home</a>
                </Link>
            </div></p>
        </div>)
    }
}
